mapper.select:
select * from `sys_user` where `real_name` = #{authorName}
and del_flag != -1
and user_id in (
  select user_id `roleUId` from `sys_map_user_role` where role_id = #{roleId}
  and `roleUId` in (
    select user_id `orgUId` from `common_map_user_organize` where org_id = #{orgId}
    and `orgUId` in (
      select user_id `subUId` from `common_map_user_subject` where subject_id = #{subId}
    )
  )
)

select * from docmanager.sys_user `sUser` where sUser.id in (
  select role.user_id from docmanager.sys_map_user_role `role` where role_id in (
    select id from docmanager.sys_role where name = '学生' or name = '教师'
  )
);
