mapper.select:
select name_cn from docmanager.sys_dict `dict` where dict.type_id = (
  select id from docmanager.sys_dict_type `type` where type.name_cn = '作者身份'
)

select *from docmanager.doc_paper
where  `del_flag` != -1

    and `paper_name` = #{paperName}


    and `ISSN` = #{ISSN}


    and `store_Num` = #{storeNum}


    and `doc_type` = #{docType}


    and id in (
    select paper_id from docmanager.doc_map_user_paper `map` where map.author_type_id = (
    select `id` from docmanager.sys_dict `dict` where dict.type_id = (
    select `id` from docmanager.sys_dict_type `type` where  type.name_cn = '作者身份'
    ) and dict.name_cn = '第一作者'
    )
    and user_id = (select `id` from Sys_User_Info where user_workID = #{firstAuthorWorkNum})
    )


    and id in (
    select paper_id from docmanager.doc_map_user_paper `map` where map.author_type_id = (
    select `id` from docmanager.sys_dict `dict` where dict.type_id = (
    select `id` from docmanager.sys_dict_type `type` where  type.name_cn = '作者身份'
    ) and dict.name_cn = '第二作者'
    )
    and user_id = (select `id` from Sys_User_Info where user_workID = #{secondAuthorWorkNum})
    )


    and id in (
    select paper_id from docmanager.doc_map_user_paper `map` where map.author_type_id = (
    select `id` from docmanager.sys_dict `dict` where dict.type_id = (
    select `id` from docmanager.sys_dict_type `type` where  type.name_cn = '作者身份'
    ) and dict.name_cn = '其他作者'
    )
    and user_id = (select `id` from Sys_User_Info where user_workID = #{otherAuthorWorkNum})
    )

select name_en,id from docmanager.sys_dict where type_id = (
  select id from docmanager.sys_dict_type where sys_dict_type.name_cn = '论文种类'
) and del_flag != -1

select * from docmanager.doc_paper pp
where pp.del_flag != -1

    and pp.paper_name like concat('%',#{paperName},'%')


    and pp.issn = #{issn}


    and pp.store_Num = #{storeNum}


    and pp.doc_Type = #{docType}


    and pp.first_author_id = (select id from `sys_user` where work_id = #{firstAuthorWorkNum})


    and pp.second_author_id = (select id from `sys_user` where work_id = #{secondAuthorWorkNum})


    and pp.id in (
    select paper_id from doc_map_user_paper mmp where mmp.user_id = (
    select id from sys_user where work_id = #{otherAuthorWorkNum}
    ) and mmp.author_type_id = (
    select id from `sys_dict` where type_id = (
    select id FROM `sys_dict_type` where name_cn = '作者身份'
    ) and name_cn = '其他作者'
    )
    )

limit #{pageIndex},#{pageSize}
